    private void ControlBotonVaciar(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ControlBotonVaciar
        lIzquierda.removeAll();
        lDerecha.removeAll();
    }//GEN-LAST:event_ControlBotonVaciar

