    private void ControlBotonAdd(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ControlBotonAdd
       lIzquierda.add(tAdd.getText());
       tAdd.setText(null);
       tAdd.requestFocus();
    }//GEN-LAST:event_ControlBotonAdd

    private void ControlBotonBorrar(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ControlBotonBorrar
        try{
            lIzquierda.remove(lIzquierda.getSelectedIndex());
        }catch(ArrayIndexOutOfBoundsException e){}
    }//GEN-LAST:event_ControlBotonBorrar

    // Despues de que el usuario ingresa una nueva quiniela se genera un numero 1 , 2 o x
    private void ControlBotonGenerar(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ControlBotonGenerar

        for (int i = 0; i < lIzquierda.getItemCount() ; i++) {
            int num = (int) (Math.random() * 100+1);
            if (num <= 60) {
                    lDerecha.add(lIzquierda.getItem(i)+" 1 ");
            }else if(num <= 90){
                     lDerecha.add(lIzquierda.getItem(i)+" X ");
            }else{
                    lDerecha.add(lIzquierda.getItem(i)+" 2 ");
            }
        }
    }//GEN-LAST:event_ControlBotonGenerar

    private void tAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tAddActionPerformed
        lIzquierda.add(tAdd.getText());
        tAdd.setText(null);
        tAdd.requestFocus();
    }//GEN-LAST:event_tAddActionPerformed
    
